import React, { useState } from "react";
import Field from "./partials/Field";
import Button from "./partials/Button";
import { submitBtn, editBtn, cancelBtn } from "./Data";
// import generateId from "../helpers/idGenerator";

import "./Main.css";

const generateId = (text) => {
  return text.replace(/\s+/g, "").toLowerCase();
};

const Form = ({ title, fields }) => {
  const fieldsWithId = fields.map((field) => ({
    ...field,
    id: field.id ? field.id : generateId(field.label)
  }));
  const [inputs, setInputs] = useState(fieldsWithId);
  const checkData = () => {
    const rtn = inputs.filter(
      (input) => input.value !== undefined || input.value !== "")
    // ).length > 0
    //   ? true
    //   : false;
    console.log(rtn);
  };
  const submitControl = checkData() ? editBtn : submitBtn;
  const handleChange = (event) => {
    let index = inputs.findIndex((element) => element.id === event.target.id);
    let newInputs = [...inputs];
    newInputs[index] = { ...newInputs[index], value: event.target.value };
    setInputs(newInputs);
  };
  const handleClear = () => {
    let newInputs = inputs.map(input => ({...input, value: ""}));
    setInputs(newInputs);
  };
  const handleSubmit = () => {
    const output = inputs.map(({ label, value }) => `${label}: ${value}\r\n`);
    handleClear();
    console.log(...output);
  };
  return (
    <div className="form">
      <div className="form__header">
        <h2> {title} </h2>
      </div>
      <div className="form__content">
        {inputs.map((field) => (
          <Field key={field.label} {...field} handleChange={handleChange} />
        ))}
      </div>
      <div className="form__controls">
        <Button {...cancelBtn} handleClick={handleClear}/>
        <Button {...submitControl} handleClick={handleSubmit} />
      </div>
    </div>
  );
};

Form.defaultProps = {
  title: "My Form"
};

export default Form;
