import React from "react";

import "./Field.css";

const supportedTypes = ["text", "number", "password", "date"];

const checkType = (type) => {
  return supportedTypes.includes(type) ? type : supportedTypes[0];
};

const Field = ({ label, type, required, id, handleChange, value, error }) => {
  const errorClass = error ? "input_error" : "input_error_holder";
  return (
    <React.Fragment>
      <label htmlFor={id}>{label}</label>
      <input
        id={id}
        type={checkType(type)}
        onChange={handleChange}
        value={value}
        required={required}
      />
      <p className={errorClass}>
        {error}
      </p>
    </React.Fragment>
  );
};

export default Field;
